package day18.ifstatement;

public class ifLadder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num1=56,num2=32;
if(num1<num2) {
	System.out.println("32 is smaller than 56");
}
else if(num1>num2) {
	System.out.println("56 is greater");
}
else {
	System.out.println("Program ends");
}
	}
}
